struct ucil_theora_video_file_object {
   ogg_stream_state os;
   theora_info ti;
   theora_state th;
   FILE *f;

   int blocking_mode;

   int downsize;

   unicap_format_t format;

   int fill_frames;
   unsigned long long frame_interval;
   struct timeval last_frame_time;
   struct timeval recording_start_time;
   int frame_count;
   ogg_packet op;

   pthread_t encoder_thread;
   int quit_thread;
   GQueue *full_queue;
   GQueue *empty_queue;
   unicap_data_buffer_t *last_frame;

   unicap_new_frame_callback_t encode_frame_cb;
   void *encode_frame_cb_data;

   vorbis_info      vi;
   vorbis_dsp_state vd;
   ogg_stream_state vo;
   vorbis_block     vb;


   int          audio;
   void        *audio_data;
   char         audio_card[64];
   unsigned int audio_rate;
   int          vorbis_bitrate;
   unsigned int total_samples;
   int          async_audio_encoding;
   FILE        *audiof;

   int nocopy;

   sem_t sema;      
   sem_t lock;

   int requires_resizing_frames;
};
